INSERT INTO public.shop_product (id, model, description, price, image, quantity, collection, date, manufacture_id) VALUES (19, 'Certina C034.453.11.047.10', 'Эта ограниченная серия посвящена команде Monster Energy Yamaha Factory MXGP и с гордостью носит ее цвета и логотип. Спортивная дисциплина этой команды отличается невероятной скоростью, что умело передает этот высококачественный хронограф, оснащенный таймером на круг и способный отображать десятые доли секунды.', 3317.31, 'product_image/Certina_Sport_C034.453.11.047.10_Jx7Ijdg.jpg', 7, 'Sport', '2023-06-04', 4);
INSERT INTO public.shop_product (id, model, description, price, image, quantity, collection, date, manufacture_id) VALUES (20, 'Certina C007.410.17.051.00', 'Отличные часы для любителя дайвинга. К погружению и в море, и в стиль готовы! Для истинного джентльмена, способного в свободное время перевоплощаться в экстремального спортсмена-аквалангиста.', 2546.43, 'product_image/Certina_DS_Blue_Ribbon_C007.410.17.051.00_Yj4l8gp.jpg', 8, 'DS Blue Ribbon', '2023-06-04', 4);
