INSERT INTO public.shop_product (id, model, description, price, image, quantity, collection, date, manufacture_id) VALUES (24, 'Orient FTT0U002F0', 'Мужские часы Orient с зеленым циферблатом в прочном стальном корпусе на браслете. Календарь: число. Диаметр корпуса 43 мм.', 454.44, 'product_image/Orient_Neo_70s_FTT0U002F0_uv3kQBJ.jpg', 10, 'Neo 70s', '2023-06-04', 3);
INSERT INTO public.shop_product (id, model, description, price, image, quantity, collection, date, manufacture_id) VALUES (25, 'Orient RA-AA0008B19B', 'Мужские механические часы с автоподзаводом в корпусе из нержавеющей стали. Продуманный дизайн для подводного плавания: завинчивающаяся заводная головка и однонаправленно вращающийся безель - эти часы станут надежным аксессуаром. Указатель даты и дня недели в районе 3 часов.', 1354.60, 'product_image/Orient_Automatic_RA-AA0008B19B_niFFhOd.jpg', 5, 'Automatic', '2023-06-04', 3);
INSERT INTO public.shop_product (id, model, description, price, image, quantity, collection, date, manufacture_id) VALUES (26, 'Hamilton FROGMAN AUTO  H77605135', 'Первые модели Hamilton Frogman были разработаны специально для подразделения аквалангистов американских ВМС. В 1951 году часы использовались в съемках нашумевшего фильма «Водолазы». С тех пор прошло почти 70 лет, технологии часового производства были значительно усовершенствованы. При этом неизменными остались жизненно важные функции, благодаря которым часы обрели столь же широкую популярность, как и фильм: вращающийся в одном направлении безель, оптимальная водонепроницаемость, предусмотренное для использования под водой люминесцентное покрытие.', 4385.49, 'product_image/FROGMAN_AUTO.jpg', 18, 'Khaki Navy', '2023-06-22', 6);
INSERT INTO public.shop_product (id, model, description, price, image, quantity, collection, date, manufacture_id) VALUES (27, 'Hamilton Jazzmaster H32612555', 'Классические кварцевые мужские часы на кожаном ремне с корпусом из нержавеющей стали.
Функции: дата, хронограф.', 3485.72, 'product_image/Hamilton_Jazzmaster.jpg', 20, 'Jazzmaster', '2023-06-22', 6);
INSERT INTO public.shop_product (id, model, description, price, image, quantity, collection, date, manufacture_id) VALUES (28, 'Hamilton Khaki Navy H82505140', 'Эти вдохновленные океаном часы одновременно изысканные и спортивные. Они идеально подойдут как для размеренного отдыха на пляже, так и для покорения морских глубин.', 4722.01, 'product_image/Hamilton_Khaki_Navy_H82505140.jpg', 12, 'Khaki Navy', '2023-06-22', 6);
INSERT INTO public.shop_product (id, model, description, price, image, quantity, collection, date, manufacture_id) VALUES (29, 'Hamilton Jazzmaster H32705152', 'Jazzmaster Open Heart 42 мм – это выверенная гармония новаторства и традиций. Скелетонизированная конструкция открывает взгляду собственный автоматический механизм швейцарского производства, скрытый за эффектным серебристым циферблатом с синими индексами и стрелками. Вы можете персонализировать часы, добавив к ним ремешок из кожи синего цвета или элегантный браслет из нержавеющей стали.', 4934.56, 'product_image/Hamilton_Jazzmaster_H32705152.jpg', 8, 'Jazzmaster', '2023-06-22', 6);
INSERT INTO public.shop_product (id, model, description, price, image, quantity, collection, date, manufacture_id) VALUES (30, 'Hamilton Chrono Quartz H37512731', 'Ничто не в силах нарушить неумолимый ход времени. Однако, выдержанные в спортивном стиле часы с вращающимся безелем, позволят обрести контроль над временем. Высокое качество и безупречная точность кварцевого механизма – гарант надежности моделей, гармонично сочетающих в себе морской стиль и классическое часовое искусство.', 3407.78, 'product_image/Hamilton_Chrono_Quartz_H37512731.png', 9, 'Jazzmaster', '2023-06-22', 6);
INSERT INTO public.shop_product (id, model, description, price, image, quantity, collection, date, manufacture_id) VALUES (31, 'Hamilton Spirit of Liberty H42415551', 'Дух свободы с новой силой проявляется в автоматических моделях Spirit of Liberty, выдержанных в классическом американском стиле, который отличает изделия Hamilton с 1892 года, с момента основания компании в штате Пенсильвания. Благодаря использованию часовых технологий XXI века, позволивших довести уровень запаса хода автоматического механизма H-10 до 80 часов, теперь можно на все выходные выпасть из времени, а в понедельник вновь надеть на запястье свои безупречно функционирующие часы.', 4137.52, 'product_image/Hamilton_Spirit_of_Liberty_H42415551.jpg', 20, 'American Classic', '2023-06-22', 6);
INSERT INTO public.shop_product (id, model, description, price, image, quantity, collection, date, manufacture_id) VALUES (32, 'Hamilton Auto Chrono H32596141', 'Часы Jazzmaster Auto Chrono легко вписываются в современный темп жизни, не изменяя при этом своему внутреннему ритму. Модель создана для ценителей традиций, способных по достоинству оценить инновационные решения. Калибр H-21 с 60-часовым запасом хода помещен в корпус с изящными изгибами и лаконичными очертаниями.', 7527.62, 'product_image/Hamilton_Auto_Chrono_H32596141.jpg', 15, 'Jazzmaster', '2023-06-22', 6);
INSERT INTO public.shop_product (id, model, description, price, image, quantity, collection, date, manufacture_id) VALUES (33, 'Hamilton Jazzmaster H32546781', 'Элегантные мужские механические часы с автоподзаводом на кожаном ремне с корпусом из нержавеющей стали с PDV покрытием розового цвета.
Функции: хронограф, дата. Прочное сапфировое стекло с антибликовым покрытием. Водозащита - 100 м. Прозрачная задняя крышка.
Размер корпуса: 42 мм.
Гарантия: 2 года.', 7332.76, 'product_image/Hamilton_Jazzmaster_H32546781.jpg', 10, 'Jazzmaster', '2023-06-22', 6);
INSERT INTO public.shop_product (id, model, description, price, image, quantity, collection, date, manufacture_id) VALUES (34, 'Casio Edifice EFV-600D-2A', 'Надежный хронограф из серии Edifice станет вашим верным спутником в вопросах времени. Edifice. Необритовое светонакопительное покрытие обеспечивает длительное послесвечение в темноте даже после кратковременного нахождения на свету. Часы-хронограф с секундомером с точностью показаний 1/10с и временем измерения 1ч. Циферблат с окошком даты. Задняя крышка с винтовым фиксатором. Заводная головка с защитой. Раскладывающаяся застежка, расстегиваемая одним касанием. Предохранительная защелка на браслете препятствует случайному расстегиванию.', 566.42, 'product_image/Casio_Edifice_EFV-600D-2A.png', 32, 'Edifice', '2023-06-22', 1);
INSERT INTO public.shop_product (id, model, description, price, image, quantity, collection, date, manufacture_id) VALUES (35, 'Casio Edifice EFV-550D-1A', 'Кварцевый хронограф. Водостойкость WR 100. Часы, минуты, секунды, дата, хронограф. Люминесцентные стрелки и метки. Корпус выполнен из нержавеющей стали с частичным IP покрытием. Размеры корпуса 47 мм х 53 мм, толщина 12 мм. Минеральное стекло. Браслет выполнен из нержавеющей стали. Срок службы батареи: 3 года.', 580.59, 'product_image/Casio_Edifice_EFV-550D-1A.png', 14, 'Edifice', '2023-06-22', 1);
INSERT INTO public.shop_product (id, model, description, price, image, quantity, collection, date, manufacture_id) VALUES (36, 'Casio Edifice EFS-S610DB-1A', 'Стильный мужской хронограф Edifice из серии EFS-S610 в стальном корпусе с черным циферблатом и красными акцентами на нем. Текстурный паутинчатый циферблат часов защищает сверхпрочное сапфировое стекло, а благодаря солнечной батарее, вы можете забыть о необходимости замены элемента питания. Модель обладает 30 минутным секундомером и стандартной для эдифайсов водозащитой до 100 метров.', 1083.62, 'product_image/Casio_Edifice_EFS-S610DB-1A.png', 17, 'Edifice', '2023-06-22', 1);
INSERT INTO public.shop_product (id, model, description, price, image, quantity, collection, date, manufacture_id) VALUES (37, 'Tissot Quickster NATO T095.417.17.037.09', 'Уникальность этой модели заключается в простоте ее циферблата и разноцветном безеле, а также в модных ремешках NATO. Ремешок часов легко меняется в зависимости от настроения или выбранной одежды. Дополнительные функции: Секундомер, тахиметр, подсветка стрелок.', 1772.97, 'product_image/Tissot_Quickster_NATO_T095.417.17.037.09.jpg', 8, 'T-Sport', '2023-06-22', 2);
INSERT INTO public.shop_product (id, model, description, price, image, quantity, collection, date, manufacture_id) VALUES (38, 'Tissot T-Touch Expert Titanium T013.420.44.057.00', 'Уникальный современный дизайн очень удачно дополняет черный циферблат. Стрелки этих часов движет надежный электронный, кварцевый механизм, который отличается изумительной точностью. Для изготовления корпуса используется специальный высококачественный материал- титан.', 4693.32, 'product_image/Tissot_T-Touch_Expert_Titanium_T013.420.44.057.00.png', 5, 'T-Touch', '2023-06-22', 2);
INSERT INTO public.shop_product (id, model, description, price, image, quantity, collection, date, manufacture_id) VALUES (39, 'Tissot Luxury Powermatic 80 T086.407.16.057.00', 'Главный секрет этих часов – в новом революционном механизме Powermatic 80 с беспрецедентным уровнем запаса хода, составляющим 80 часов. Высочайшему уровню качества, заданному механизмом, соответствует и дизайн часов, отличающийся изысканностью и истинным классом, перед которым невозможно устоять.', 2991.55, 'product_image/Tissot_Luxury_Powermatic_80_T086.407.16.057.00.png', 6, 'T-Classic', '2023-06-22', 2);
INSERT INTO public.shop_product (id, model, description, price, image, quantity, collection, date, manufacture_id) VALUES (40, 'Orient Sporty FKU00002D0', 'Стильные мужские часы-хронограф с индикатором даты и люминесцентными стрелками. Завинчивающаяся задняя крышка. Браслет с раскладывающейся застежкой.', 654.63, 'product_image/Orient_Sporty_FKU00002D0.png', 26, 'Sporty', '2023-06-22', 3);
