INSERT INTO public.shop_product (id, model, description, price, image, quantity, collection, date, manufacture_id) VALUES (43, 'Certina DS 2 C024.618.11.051.01', 'Этот 43 мм хронограф с кварцевый механизмом высокой точности Precidrive™ и функцией Flyback позволяет запускать, останавливать и сбрасывать время нажатием одной кнопки. Дополнительная стрелка хронографа позволяет одновременно замерять несколько временных промежутков.', 3747.85, 'product_image/Certina_DS_2_C024.618.11.051.01.jpg', 14, 'DS 2', '2023-06-22', 4);
INSERT INTO public.shop_product (id, model, description, price, image, quantity, collection, date, manufacture_id) VALUES (44, 'Certina DS Podium Chronograph C001.647.4', 'В этой модели сочетаются технические инновации и традиции часового мастерства. Сложные функции, такие как, например, дополнительный циферблат, показывающий время с точностью до 1/100 сек., и новейшие материалы в сочетании с ультраточным калибром последнего поколения Precidrive™. Окошко индикации даты в положении "6" часов. Тахиметрическая шкала. Индикатор окончания срока службы элемента питания (EOL). Завинчивающаяся заводная головка с системой защиты. Завинчивающаяся задняя крышка корпуса.', 4073.76, 'product_image/Certina_DS_Podium_Chronograph_1-100_sec_C001.647.4.jpg', 8, 'DS Podium', '2023-06-22', 4);
INSERT INTO public.shop_product (id, model, description, price, image, quantity, collection, date, manufacture_id) VALUES (45, 'Certina DS 1 C006.414.16.031.00', 'Функции: Хронограф, дата, тахиметр. Оформление: Антибликовое покрытие стекла, прозрачная задняя крышка.', 6445.04, 'product_image/Certina_DS_1_C006.414.16.031.00.jpg', 18, 'DS 1', '2023-06-22', 4);
INSERT INTO public.shop_product (id, model, description, price, image, quantity, collection, date, manufacture_id) VALUES (46, 'Hublot Blue Titanium Bracelet 510.NX.7170.NX', 'Fusion – имя говорит само за себя. Эстетика Classic Fusion, наиболее изящной и элегантной из коллекций бренда, вдохновлена первыми часами Hublot начала 1980-х годов. В них пользовались успехом круглый корпус с закрепленным на видимых винтах безелем и характерный каучуковый ремешок. Современные версии Classic Fusion выполнены с использованием прогрессивных материалов, а силуэт этих авангардных по исполнению, но классических по сути часов по-настоящему элегантен. Корпус из сатинированного и полированного титана, сатинированный и полированный титановый безель с 6-тью Н-образными винтами, прозрачная задняя крышка. Синий сатинированный циферблат с узором «солнечные лучи». Браслет из сатинированного и полированного титана с раскладывающейся застежкой. Калибр: HUB1112, запас хода: 42 часа.', 38640.49, 'product_image/Hublot_Blue_Titanium_Bracelet_510.NX.7170.NX.jpg', 4, 'Classic Fusion', '2023-06-22', 5);
INSERT INTO public.shop_product (id, model, description, price, image, quantity, collection, date, manufacture_id) VALUES (47, 'Hublot Black Magic 510.CM.1170.CM', 'Fusion – имя говорит само за себя. Эстетика Classic Fusion, наиболее изящной и элегантной из коллекций бренда, вдохновлена первыми часами Hublot начала 1980-х годов. В них пользовались успехом круглый корпус с закрепленным на видимых винтах безелем и характерный каучуковый ремешок. Современные версии Classic Fusion выполнены с использованием прогрессивных материалов, а силуэт этих авангардных по исполнению, но классических по сути часов по-настоящему элегантен. Корпус из сатинированной черной керамики, вертикально сатинированный керамический безель с 6-тью Н-образными винтами, прозрачная задняя крышка. Матовый черный циферблат, накладные индексы и полированные стрелки с родиевым покрытием. Черный браслет из полированной и сатинированной керамики с раскладывающейся застежкой из нержавеющей стали с черным покрытием. Калибр: HUB1143, запас хода: 42 часа.', 54708.82, 'product_image/Hublot_Black_Magic_510.CM.1170.CM.jpg', 4, 'Classic Fusion', '2023-06-22', 5);
